controller.down.onEvent(ControllerButtonEvent.Pressed, function () {
    if (!(full)) {
        isbreathing = true
        controller.moveSprite(kirby, 25, 0)
        characterAnimations.setCharacterAnimationsEnabled(kirby, false)
        kirby.y += -7
        while (controller.down.isPressed()) {
            animation.runImageAnimation(
            kirby,
            [img`
                .......ffff.....
                .....ff2332ff...
                ..fff23333332f..
                .f233f33333f32f.
                f23332333333f3f.
                f33333333333f32f
                f33333333333333f
                f33333333333332f
                f2333333332f32f.
                .f2333333333ff..
                f233333333332f..
                f333333333333f..
                f333333333333f..
                f333333333333f..
                f233333333333f..
                .f33333333332ff.
                .f2333333332f33f
                ..f233333333332f
                ..fff223333322ff
                ..f22fffffffff..
                ...f2222222f....
                ....fffffff.....
                `],
            75,
            true
            )
            for (let value of sprites.allOfKind(SpriteKind.Enemy)) {
                if (Math.abs(kirby.x - value.x) < 40) {
                    value.follow(kirby, 75)
                }
                if (value.overlapsWith(kirby)) {
                    sprites.destroy(value, effects.rings, 200)
                    powerup = true
                    full = true
                    break;
                }
            }
            for (let value2 of sprites.allOfKind(SpriteKind.Food)) {
                if (Math.abs(kirby.x - value2.x) < 40 && Math.abs(kirby.y - value2.y) < 30) {
                    value2.follow(kirby, 125)
                }
                if (value2.overlapsWith(kirby)) {
                    sprites.destroy(value2, effects.rings, 200)
                    powerup = true
                    full = true
                    break;
                }
            }
            for (let value3 of sprites.allOfKind(SpriteKind.enemy2)) {
                if (Math.abs(kirby.x - value3.x) < 40) {
                    value3.follow(kirby, 125)
                }
                if (value3.overlapsWith(kirby)) {
                    sprites.destroy(value3, effects.rings, 200)
                    powerup = true
                    full = true
                    break;
                }
            }
            for (let value4 of sprites.allOfKind(SpriteKind.enemy4)) {
                if (Math.abs(kirby.x - value4.x) < 40) {
                    value4.follow(kirby, 125)
                }
                if (value4.overlapsWith(kirby)) {
                    sprites.destroy(value4, effects.rings, 200)
                    powerup = true
                    full = true
                    eatplayer2 = true
                    break;
                }
            }
            pause(1)
        }
        animation.stopAnimation(animation.AnimationTypes.All, kirby)
        characterAnimations.setCharacterAnimationsEnabled(kirby, true)
        controller.moveSprite(kirby, 65, 0)
        isbreathing = false
    }
})
